mapper.select:
SELECT zzw_item.*
FROM zzw_item
WHERE zzw_item.library_code = #{libraryCode}

mapper.update:
UPDATE zzw_item
SET m_loan = #{mLoan, jdbcType=INTEGER}
WHERE id = #{id}

UPDATE zzw_item
SET m_reservation = #{mReservation, jdbcType=INTEGER}
WHERE id = #{id}

UPDATE zzw_item
SET m_lose_book = #{mLoseBook, jdbcType=INTEGER}
WHERE id = #{id}
